import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import TokenPurchaseModal from './TokenPurchaseModal';
import AIHelpModal from './AIHelpModal';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  Legend
} from 'recharts';
import {
  CircleDollarSign,
  Clock,
  Download,
  FileText,
  Filter,
  HelpCircle,
  History,
  Image,
  Info,
  MessageSquare,
  Plus,
  Search,
  Settings,
  ShoppingCart
} from 'lucide-react';

// 模擬數據 - 在實際應用中應從API獲取
const mockUsageData = [
  { date: '04/02', text: 1200, image: 300, total: 1500 },
  { date: '04/03', text: 800, image: 200, total: 1000 },
  { date: '04/04', text: 1500, image: 400, total: 1900 },
  { date: '04/05', text: 2000, image: 600, total: 2600 },
  { date: '04/06', text: 1800, image: 500, total: 2300 },
  { date: '04/07', text: 1000, image: 300, total: 1300 },
  { date: '04/08', text: 1600, image: 400, total: 2000 }
];

const mockUsageHistory = [
  { 
    id: 1, 
    timestamp: '2025-04-08 14:23:15', 
    serviceType: 'text', 
    description: '商品描述生成', 
    tokenUsed: 150,
    product: '精品咖啡豆'
  },
  { 
    id: 2, 
    timestamp: '2025-04-08 10:45:32', 
    serviceType: 'image', 
    description: '商品圖片生成', 
    tokenUsed: 350,
    product: '手沖咖啡壺'
  },
  { 
    id: 3, 
    timestamp: '2025-04-07 16:12:08', 
    serviceType: 'text', 
    description: '商品描述生成', 
    tokenUsed: 180,
    product: '咖啡過濾器'
  },
  { 
    id: 4, 
    timestamp: '2025-04-07 11:30:45', 
    serviceType: 'image', 
    description: '商品圖片生成', 
    tokenUsed: 320,
    product: '咖啡磨豆機'
  },
  { 
    id: 5, 
    timestamp: '2025-04-06 09:15:22', 
    serviceType: 'text', 
    description: '行銷文案生成', 
    tokenUsed: 200,
    product: '母親節限定組合'
  },
  { 
    id: 6, 
    timestamp: '2025-04-05 13:45:11', 
    serviceType: 'text', 
    description: '商品描述生成', 
    tokenUsed: 160,
    product: '手工拿鐵杯'
  },
  { 
    id: 7, 
    timestamp: '2025-04-05 15:20:33', 
    serviceType: 'image', 
    description: '商品圖片生成', 
    tokenUsed: 380,
    product: '手工拿鐵杯'
  }
];

const AIServiceManagement = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredHistory, setFilteredHistory] = useState(mockUsageHistory);
  const [selectedServiceType, setSelectedServiceType] = useState('all');
  const [timeRange, setTimeRange] = useState('7days');
  const [usageData, setUsageData] = useState(mockUsageData);
  const [isPurchaseModalOpen, setIsPurchaseModalOpen] = useState(false);
  const [isHelpModalOpen, setIsHelpModalOpen] = useState(false);

  // 模擬的Token餘額
  const remainingTokens = 12500;
  const totalTokens = 20000;
  const usedTokens = totalTokens - remainingTokens;
  const usagePercentage = (usedTokens / totalTokens) * 100;

  // 處理搜尋和篩選邏輯
  useEffect(() => {
    let filtered = mockUsageHistory;

    // 處理搜尋詞
    if (searchTerm) {
      filtered = filtered.filter(item => 
        item.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.product.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // 處理服務類型篩選
    if (selectedServiceType !== 'all') {
      filtered = filtered.filter(item => item.serviceType === selectedServiceType);
    }

    setFilteredHistory(filtered);
  }, [searchTerm, selectedServiceType]);

  // 處理時間範圍變更
  useEffect(() => {
    // 實際應用中應根據選擇的時間範圍從API獲取數據
    // 這裡僅模擬不同的數據長度
    if (timeRange === '30days') {
      // 模擬30天數據
      const thirtyDaysData = [...mockUsageData];
      for (let i = 9; i <= 30; i++) {
        thirtyDaysData.push({
          date: `04/${i < 10 ? '0' + i : i}`,
          text: Math.floor(Math.random() * 2000),
          image: Math.floor(Math.random() * 600),
          get total() { return this.text + this.image; }
        });
      }
      setUsageData(thirtyDaysData);
    } else {
      setUsageData(mockUsageData);
    }
  }, [timeRange]);

  // 服務類型對應的圖標
  const serviceTypeIcon = (type) => {
    switch (type) {
      case 'text':
        return <MessageSquare className="w-5 h-5 text-blue-500" />;
      case 'image':
        return <Image className="w-5 h-5 text-purple-500" />;
      default:
        return <FileText className="w-5 h-5 text-gray-500" />;
    }
  };

  return (
    <div className="container mx-auto px-4 py-6 max-w-6xl">
      <div className="mb-6">
        <h1 className="text-2xl font-bold">AI 服務使用管理</h1>
        <p className="text-gray-600">管理您的AI服務使用狀況、查看歷史記錄並購買更多Token</p>
      </div>

      {/* Token使用概覽卡片 */}
      <div className="bg-white rounded-lg shadow p-6 mb-6">
        <div className="flex justify-between items-start">
          <div>
            <h2 className="text-lg font-semibold mb-4">Token餘額</h2>
            <div className="flex items-center gap-2 mb-1">
              <div className="text-3xl font-bold text-blue-600">{remainingTokens.toLocaleString()}</div>
              <div className="text-sm text-gray-500">/ {totalTokens.toLocaleString()} Tokens</div>
            </div>
            <p className="text-sm text-gray-600 mb-4">本月已使用 {usedTokens.toLocaleString()} Tokens</p>
          </div>

          <div className="flex gap-3">
            <button 
              className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition"
              onClick={() => setIsPurchaseModalOpen(true)}
            >
              <ShoppingCart className="w-4 h-4" />
              購買Token
            </button>
            <button 
              className="flex items-center gap-2 px-4 py-2 bg-white border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition"
              onClick={() => setIsHelpModalOpen(true)}
            >
              <Info className="w-4 h-4" />
              使用說明
            </button>
          </div>
        </div>

        {/* 進度條 */}
        <div className="w-full bg-gray-200 rounded-full h-2.5 mt-2">
          <div 
            className={`h-2.5 rounded-full ${usagePercentage > 80 ? 'bg-red-500' : 'bg-blue-500'}`}
            style={{ width: `${usagePercentage}%` }}
          ></div>
        </div>

        {/* 服務類別卡片 */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-6">
          <div className="bg-blue-50 p-4 rounded-lg border border-blue-100">
            <div className="flex justify-between">
              <div className="flex items-center gap-2">
                <MessageSquare className="w-5 h-5 text-blue-500" />
                <h3 className="font-medium">文字生成</h3>
              </div>
              <HelpCircle className="w-4 h-4 text-gray-400" />
            </div>
            <p className="text-2xl font-bold mt-2">7,500 <span className="text-sm font-normal text-gray-500">Tokens 剩餘</span></p>
            <p className="text-xs text-gray-500 mt-1">商品描述、行銷文案等文字生成服務</p>
          </div>

          <div className="bg-purple-50 p-4 rounded-lg border border-purple-100">
            <div className="flex justify-between">
              <div className="flex items-center gap-2">
                <Image className="w-5 h-5 text-purple-500" />
                <h3 className="font-medium">圖像生成</h3>
              </div>
              <HelpCircle className="w-4 h-4 text-gray-400" />
            </div>
            <p className="text-2xl font-bold mt-2">5,000 <span className="text-sm font-normal text-gray-500">Tokens 剩餘</span></p>
            <p className="text-xs text-gray-500 mt-1">商品圖片、行銷圖像等圖像生成服務</p>
          </div>

          <div className="bg-lime-50 p-4 rounded-lg border border-lime-100">
            <div className="flex justify-between">
              <div className="flex items-center gap-2">
                <CircleDollarSign className="w-5 h-5 text-lime-500" />
                <h3 className="font-medium">節省估算</h3>
              </div>
              <HelpCircle className="w-4 h-4 text-gray-400" />
            </div>
            <p className="text-2xl font-bold mt-2">約 4.2 <span className="text-sm font-normal text-gray-500">小時節省</span></p>
            <p className="text-xs text-gray-500 mt-1">基於本月使用量的時間節省估算</p>
          </div>
        </div>
      </div>

      {/* 使用趨勢圖 */}
      <div className="bg-white rounded-lg shadow p-6 mb-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-semibold">使用趨勢</h2>
          <div className="flex gap-2">
            <button 
              className={`px-3 py-1 rounded-md text-sm ${timeRange === '7days' ? 'bg-blue-100 text-blue-700' : 'bg-gray-100 text-gray-700'}`}
              onClick={() => setTimeRange('7days')}
            >
              最近 7 天
            </button>
            <button 
              className={`px-3 py-1 rounded-md text-sm ${timeRange === '30days' ? 'bg-blue-100 text-blue-700' : 'bg-gray-100 text-gray-700'}`}
              onClick={() => setTimeRange('30days')}
            >
              最近 30 天
            </button>
          </div>
        </div>

        <div className="h-80">
          <ResponsiveContainer width="100%" height="100%">
            <BarChart
              data={usageData}
              margin={{ top: 10, right: 30, left: 0, bottom: 5 }}
            >
              <CartesianGrid strokeDasharray="3 3" vertical={false} />
              <XAxis dataKey="date" />
              <YAxis />
              <Tooltip formatter={(value) => [`${value} Tokens`, '']} />
              <Legend formatter={(value) => value === 'text' ? '文字生成' : '圖像生成'} />
              <Bar dataKey="text" fill="#3b82f6" name="text" />
              <Bar dataKey="image" fill="#8b5cf6" name="image" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* 使用歷史紀錄 */}
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div className="p-6 border-b">
          <h2 className="text-lg font-semibold">使用歷史紀錄</h2>
          <p className="text-sm text-gray-500">查看所有 AI 服務使用記錄</p>

          <div className="mt-4 flex flex-col sm:flex-row gap-4">
            <div className="relative flex-grow">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
              <input
                type="text"
                placeholder="搜尋服務描述或商品..."
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            <div className="sm:w-48">
              <div className="relative">
                <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                <select
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 appearance-none bg-white"
                  value={selectedServiceType}
                  onChange={(e) => setSelectedServiceType(e.target.value)}
                >
                  <option value="all">所有服務</option>
                  <option value="text">文字生成</option>
                  <option value="image">圖像生成</option>
                </select>
                <div className="absolute inset-y-0 right-0 flex items-center pr-3 pointer-events-none">
                  <svg className="w-4 h-4 text-gray-400" viewBox="0 0 20 20" fill="currentColor">
                    <path
                      fillRule="evenodd"
                      d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
                      clipRule="evenodd"
                    />
                  </svg>
                </div>
              </div>
            </div>

            <button className="flex items-center gap-2 px-4 py-2 bg-white border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition">
              <Download className="w-4 h-4" />
              匯出紀錄
            </button>
          </div>
        </div>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  時間
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  服務類型
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  描述
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  商品
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  使用Token
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredHistory.map((item) => (
                <tr key={item.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    <div className="flex items-center gap-2">
                      <Clock className="w-4 h-4 text-gray-400" />
                      {item.timestamp}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      {serviceTypeIcon(item.serviceType)}
                      <span className="ml-2 text-sm font-medium text-gray-900">
                        {item.serviceType === 'text' ? '文字生成' : '圖像生成'}
                      </span>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {item.description}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {item.product}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    {item.tokenUsed.toLocaleString()} Tokens
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {filteredHistory.length === 0 && (
            <div className="py-8 text-center text-gray-500">
              沒有符合條件的記錄
            </div>
          )}
        </div>

        <div className="px-6 py-4 border-t bg-gray-50 flex justify-between items-center">
          <div className="text-sm text-gray-500">
            顯示 <span className="font-medium">{filteredHistory.length}</span> 筆記錄 (共 {mockUsageHistory.length} 筆)
          </div>

          <div className="flex gap-2">
            <button className="px-3 py-1 border border-gray-300 rounded-md text-sm bg-white hover:bg-gray-50">
              上一頁
            </button>
            <button className="px-3 py-1 border border-gray-300 rounded-md text-sm bg-blue-50 text-blue-700 border-blue-200">
              1
            </button>
            <button className="px-3 py-1 border border-gray-300 rounded-md text-sm bg-white hover:bg-gray-50">
              下一頁
            </button>
          </div>
        </div>
      </div>

      {/* 購買Token彈出視窗 */}
      <TokenPurchaseModal 
        isOpen={isPurchaseModalOpen}
        onClose={() => setIsPurchaseModalOpen(false)}
      />

      {/* AI服務說明彈出視窗 */}
      <AIHelpModal
        isOpen={isHelpModalOpen}
        onClose={() => setIsHelpModalOpen(false)}
      />
    </div>
  );
};

export default AIServiceManagement;
